import React from "react";
import styled from "styled-components";

const WrapperMenuPlegable = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
`;

const WrapperTitle = styled.div`
	display: flex;
`;

const WrapperTitleOptions = styled.div`
	width: 10px;
`;

const Arrow = "../../images/downarrow.png";
const ImgDownArrow = styled.img`
	margin-top: 20px;
	margin-left: 10px;
	width: 40px;
	width: 15px;
	height: 15px;
`;

const Title = styled.p``;

const List = styled.ul`
	display: none;
	list-style: none;
	padding: 15px;
	position: absolute;
	text-align: center;
	margin: -30px 0px 0px -10px;
	background-color: #2a2220;
	${WrapperMenuPlegable}:hover & {
		display: block;
	}
`;

const ItemList = styled.li`
	margin: 5px 0px;
`;

const Anchor = styled.a`
	color: black;
	text-decoration: none;
	font-family: Quicksand;
	cursor: pointer;
	${ItemList}:hover & {
		color: #2eccfa;
	}
`;

class Develop extends React.Component {
	render() {
		return (
			<WrapperMenuPlegable>
				<WrapperTitleOptions>
					<WrapperTitle>
						<Title>Solutions</Title>
						<ImgDownArrow src={Arrow} />
					</WrapperTitle>
					<List>
						<ItemList>
							<Anchor href="/">Solution 1</Anchor>
						</ItemList>
						<ItemList>
							<Anchor href="/">Solution 2</Anchor>
						</ItemList>
						<ItemList>
							<Anchor href="/">Solution 3</Anchor>
						</ItemList>
					</List>
				</WrapperTitleOptions>
			</WrapperMenuPlegable>
		);
	}
}

export default Develop;
