import styled from "styled-components";

const WrapperSolution = styled.div`
	width: 20%;
	margin: auto;
	background-color: red;
`;

const ImgSolution = styled.img`
	width: 40px;
	height: 40px;
`;

const WrapperInfo = styled.div`
	display: flex;
	flex-direction: column;
`;

const TitleInfo = styled.h3`
	margin: 5px 0px;
`;

const TextInfo = styled.p``;

const WrapperLink = styled.div``;

const ImgArrow = styled.img`
	width: 20px;
	height: 20px;
	margin-right: 10px;
	margin-bottom: -5px;
`;

const Anchor = styled.a`
	color: #2eccfa;
	text-decoration: none;
`;

export {
	WrapperSolution,
	ImgSolution,
	WrapperInfo,
	TitleInfo,
	TextInfo,
	WrapperLink,
	ImgArrow,
	Anchor,
};
